import swal from 'sweetalert'


export function showMessage(title: string, message: string) {
    swal({
        title: title,
        text: message
    })
}

export function showNotification(message: string, duration_s: number = 2.5) {
    swal(message, {
        buttons: [false, false],
        timer: duration_s * 1000
    })
}

const Alerts = {
    showMessage,
    showNotification
}

export default Alerts
