import './board.css'

import Tile, { TileColor } from '../Tile/Tile';
const BOARD_WIDTH = 8;
const BOARD_HEIGHT = 8;

export default function Board() {

  const generateBoard = () => {
    const rowContents = []
    for (let row = 0; row < BOARD_WIDTH ; row++) {
      for (let col = 0; col < BOARD_HEIGHT ; col++) {
        const tileColor = (row + col) % 2 == 0 ? TileColor.LIGHT : TileColor.DARK
        rowContents.push(<Tile color={tileColor}/>)
      }
    }
  }

  return (
    <div className="board">
      <div className="board-row">

      </div>
      <Tile color={TileColor.DARK}/>
      <Tile color={TileColor.LIGHT}/>
      <Tile color={TileColor.DARK}/>
      <Tile color={TileColor.LIGHT}/>
      <Tile color={TileColor.DARK}/>
      <Tile color={TileColor.LIGHT}/>
      <Tile color={TileColor.DARK}/>
      <Tile color={TileColor.LIGHT}/>
      <Tile color={TileColor.DARK}/>
      <Tile color={TileColor.LIGHT}/>
      <Tile color={TileColor.DARK}/>
    </div>
  );
}
