import swal from 'sweetalert'
import { ButtonList } from 'sweetalert/typings/modules/options/buttons'

interface ButtonEvent {
    className: string,
    eventName: string,
    execute: (e: Event) => void
}

interface ButtonEvents {
    buttons: List<ButtonEvent>
}

export function showMessage(
    title: string,
    message: string,
    buttons: ButtonList | (string | boolean)[] = [false, true],
    buttonEvents: ButtonEvents
) {
    const sw = swal({
        title: title,
        text: message,
        buttons: buttons
    })

    for (let i = 0; i < buttonEvents.buttons; i++) {
        be.button
    }

    return sw
}

export function showNotification(message: string, duration_s: number = 2.3) {
    return swal(message, {
        buttons: [false, false],
        timer: duration_s * 1000
    })
}

const Alerts = {
    showMessage,
    showNotification
}

export default Alerts
