import { Game } from '../reducers/root'
import Server from '../../server/server'

export const doLogin = (username: string) => {
    return {
        type: 'LOGIN',
        payload: {
            'username': username
        }
    }
}

export const doLogout = () => {
    // To remove the session cookie
    Server.logout()

    return {
        type: 'LOGOUT',
        payload: {}
    }
}

export const joinGame = (
    game: Game
) => {
    console.log("Action GOT: ", game)
    return {
        type: 'JOIN_GAME',
        payload: { game }
    }
}


export const leaveGame = () => {
    return {
        type: 'LEAVE_GAME',
        payload: {}
    }
}

const Actions = {
    doLogin,
    doLogout,
    joinGame,
    leaveGame
}

export default Actions
