import { PieceObject, PieceColor, PieceType } from './piece'
import { PositionObject, Position } from './position'

export const BOARD_WIDTH = 8;
export const BOARD_HEIGHT = 8;

export class BoardObject {

  constructor(initBoard: boolean = true) {
    if (initBoard) {
      this.initBoard()
    }
  }

  // the board itelf. a matrix with 8 rows and 8 columns
  board: Array<Array<PieceObject | null>> = Array(BOARD_HEIGHT).fill(null).map(()=>Array(BOARD_WIDTH).fill(null));

  setPieceAt = (position: PositionObject, piece: PieceObject) => this.board[position.row][position.column] = piece

  getPieceAt = (position: PositionObject) => this.board[position.row][position.column]

  // * Also a string.toBoard would be good
  toString() {
    var boardAsString: string = ""
    return this.board.map(row => row.map(col => this.getPieceAt(Position(col, row))))
  }

  initBoard() {
    // SET BLACK PIECES
    this.board[0][0] = { color: PieceColor.BLACK, type: PieceType.ROOK }; this.board[0][1] = { color: PieceColor.BLACK, type: PieceType.KNIGHT }; this.board[0][2] = { color: PieceColor.BLACK, type: PieceType.BISHOP }; this.board[0][3] = { color: PieceColor.BLACK, type: PieceType.QUEEN }; this.board[0][4] = { color: PieceColor.BLACK, type: PieceType.KING }; this.board[0][5] = { color: PieceColor.BLACK, type: PieceType.BISHOP }; this.board[0][6] = { color: PieceColor.BLACK, type: PieceType.KNIGHT }; this.board[0][7] = { color: PieceColor.BLACK, type: PieceType.ROOK }; this.board[1][0] = { color: PieceColor.BLACK, type: PieceType.PAWN }; this.board[1][1] = { color: PieceColor.BLACK, type: PieceType.PAWN }; this.board[1][2] = { color: PieceColor.BLACK, type: PieceType.PAWN }; this.board[1][3] = { color: PieceColor.BLACK, type: PieceType.PAWN }; this.board[1][4] = { color: PieceColor.BLACK, type: PieceType.PAWN }; this.board[1][5] = { color: PieceColor.BLACK, type: PieceType.PAWN }; this.board[1][6] = { color: PieceColor.BLACK, type: PieceType.PAWN }; this.board[1][7] = { color: PieceColor.BLACK, type: PieceType.PAWN };

    // SET WHITE PIECES
    this.board[6][0] = { color: PieceColor.WHITE, type: PieceType.PAWN }; this.board[6][1] = { color: PieceColor.WHITE, type: PieceType.PAWN }; this.board[6][2] = { color: PieceColor.WHITE, type: PieceType.PAWN }; this.board[6][3] = { color: PieceColor.WHITE, type: PieceType.PAWN }; this.board[6][4] = { color: PieceColor.WHITE, type: PieceType.PAWN }; this.board[6][5] = { color: PieceColor.WHITE, type: PieceType.PAWN }; this.board[6][6] = { color: PieceColor.WHITE, type: PieceType.PAWN }; this.board[6][7] = { color: PieceColor.WHITE, type: PieceType.PAWN }; this.board[7][0] = { color: PieceColor.WHITE, type: PieceType.ROOK }; this.board[7][1] = { color: PieceColor.WHITE, type: PieceType.KNIGHT }; this.board[7][2] = { color: PieceColor.WHITE, type: PieceType.BISHOP }; this.board[7][3] = { color: PieceColor.WHITE, type: PieceType.QUEEN }; this.board[7][4] = { color: PieceColor.WHITE, type: PieceType.KING }; this.board[7][5] = { color: PieceColor.WHITE, type: PieceType.BISHOP }; this.board[7][6] = { color: PieceColor.WHITE, type: PieceType.KNIGHT }; this.board[7][7] = { color: PieceColor.WHITE, type: PieceType.ROOK };
  }
}

