import swal from 'sweetalert'
import { getButtonListOpts } from 'sweetalert/typings/modules/options/buttons'


export function showMessage(title: string, message: string) {
    return swal({
        title: title,
        text: message,
        buttons: getButtonListOpts.call(2, true)
    })
}

export function showNotification(message: string, duration_s: number = 2.3) {
    return swal(message, {
        buttons: [false, false],
        timer: duration_s * 1000
    })
}

const Alerts = {
    showMessage,
    showNotification
}

export default Alerts
