import swal from 'sweetalert'


export function showMessage(title: string, message: string) {
    swal({
        title: title,
        text: message
    })
}

export function showNotification(message: string, duration: number = 3500) {
    console.log(duration)
    swal(message, {
        buttons: undefined,
        timer: duration
    })
}

const Alerts = {
    showMessage,
    showNotification
}

export default Alerts
