import './board.css'

import Tile, { TileColor } from '../Tile/Tile';
import { BoardObject, stringToBoard } from '../../../domain/board'
import { Position, PositionObject } from '../../../domain/position'
import { BoardError } from '../../../domain/errors';
import { Piece } from '../../../domain/piece';
import { useState } from 'react';

interface TileWrapper { tile: PositionObject | null }

const initSelectedTile: TileWrapper = { tile: null }
const initHighlightedTile: TileWrapper = { tile: null }

export default function Board(
  props: {
    boardString?: string,
    onError: (error: BoardError) => void
  }
) {

  const [selectedTile, setSelectedTile] = useState(initSelectedTile)
  const [highlightedTile, setHighlightedTile] = useState(initHighlightedTile)

  const board = getBoardFromProps()

  function getBoardFromProps(): BoardObject {
    const boardAsString: string | undefined = props.boardString

    if (boardAsString == undefined)
      return new BoardObject()

    try {
      const convertedBoard = stringToBoard(boardAsString)
      return convertedBoard
    } catch (e: any) {
      const err = e as BoardError
      err.message = `[DEBUG] Invalid Board: ${boardAsString.length} => ${boardAsString}\nOriginal Error: ${err.message}`
      props.onError(err)
    }
    return new BoardObject()
  }

  const handleTileClick = (row: number, col: number, piece: Piece | null) => {
    console.log(`Clicked on ${col}:${row} | ${piece}`)
  }

  const calcTileColor = (row: number, col: number) => (col + row) % 2 === 0 ? TileColor.LIGHT : TileColor.DARK
  const isHighlighted = (row: number, col: number) => (row == highlightedTile.tile?.row && col == highlightedTile.tile?.column)
  const isSelected = (row: number, col: number) => (row == selectedTile.tile?.row && col == selectedTile.tile?.column)

  function buildTile(row: number, column: number, piece: Piece | null) {
    return (
      <Tile
        piece={board.getPieceAt(Position(column, row))}

        // Tile state
        selected={isSelected(row, column)}
        highlighted={isHighlighted(row, column)}

        // DARK or LIGHT 
        color={calcTileColor(row, column)}

        // Position
        position={Position(column, row)}

        // Events
        onTileClick={() => handleTileClick(row, column, piece)}
      />
    )
  }

  return (
    <div className="board">
      <div>
        {
          board?.board.map((row, rowIdx) => {
            return (
              <div className="board-row">
                {
                  row.map((piece, colIdx) => buildTile(rowIdx, colIdx, piece))
                }
              </div>
            )
          }
          )
        }
      </div>
    </div>
  );
}
