import './board.css'

import Tile, { TileColor } from '../Tile/Tile';
import { BoardObject, stringToBoard } from '../../../domain/board'
import { Position, PositionObject } from '../../../domain/position'
import { BoardError } from '../../../domain/errors';
import { Piece, PieceColor } from '../../../domain/piece';
import { useState } from 'react';
import Alerts from '../../Alerts/sa-alerts';

interface TileWrapper { tile: PositionObject | null }

const initSelectedTile: TileWrapper = { tile: null }
const initTargetTiles: PositionObject[] = []

export default function Board(
  props: {
    boardString?: string,
    local_player_pieces: PieceColor
    onError: (error: BoardError) => void
  }
) {

  const [selectedTile, setSelectedTile] = useState(initSelectedTile)
  const [targetTiles, setTargetTiles] = useState(initTargetTiles)

  const board = getBoardFromProps()

  function getBoardFromProps(): BoardObject {
    const boardAsString: string | undefined = props.boardString

    if (boardAsString == undefined)
      return new BoardObject()

    try {
      const convertedBoard = stringToBoard(boardAsString)
      return convertedBoard
    } catch (e: any) {
      const err = e as BoardError
      err.message = `[DEBUG] Invalid Board: ${boardAsString.length} => ${boardAsString}\nOriginal Error: ${err.message}`
      props.onError(err)
    }
    return new BoardObject()
  }

  function buildTile(row: number, column: number, piece: Piece | null) {
    return (
      <Tile
        piece={board.getPieceAt(Position(column, row))}

        // Tile state
        selected={isSelected(row, column)}
        target={isTarget(row, column)}

        // DARK or LIGHT 
        color={calcTileColor(row, column)}

        // Position
        position={Position(column, row)}

        // Events
        onTileClick={() => handleTileClick(row, column, piece)}
      />
    )
  }

  const calcTileColor = (row: number, col: number) =>
    (col + row) % 2 === 0 ? TileColor.LIGHT : TileColor.DARK

  const isTarget = (row: number, col: number) =>
    targetTiles.find((pos) => pos.row === row && pos.column === col) != undefined

  const isSelected = (row: number, col: number) =>
    (row == selectedTile.tile?.row && col == selectedTile.tile?.column)

  const handleTileClick = (row: number, col: number, piece: Piece | null) => {
    setSelectedTile({ tile: null })
    setTargetTiles([])

    if (board.turn != props.local_player_pieces) {
      Alerts.showNotification("Not your turn. Wait for the opponent to make his move")
      return
    }

    const clickPosition = Position(col, row)
    console.log(`Clicked on ${col}:${row} | ${piece}`)

    // 1. TILE IS SELECTED
    if (selectedTile?.tile != null) {

      // CLICKED SELECTED TILE AGAIN. JUST UNSELECT
      if (isSelected(clickPosition.row, clickPosition.column)) {
        return
      }
      // CLICKED A TARGET - MAKE MOVE
      if (isTarget(clickPosition.row, clickPosition.column)) {
        console.log("TARGET")
        return
      }

      // 

      if (board.getPieceAt(clickPosition)?.color == props.local_player_pieces) {
        setSelectedTile({ tile: clickPosition })
        const targets = Array.from(board.generateAllPossibleTargets(clickPosition))
        setTargetTiles(targets)
      }
    }
    // 2. TILE IS NOT SELECTED
    else {
      // ONE OF MY TILES
      if (board.getPieceAt(clickPosition)?.color == props.local_player_pieces) {
        setSelectedTile({ tile: clickPosition })
        const targets = Array.from(board.generateAllPossibleTargets(clickPosition))
        setTargetTiles(targets)
      }
    }
  }

  return (
    <div className="board">
      <div>
        {
          board?.board.map((row, rowIdx) => {
            return (
              <div className="board-row">
                {row.map((piece, colIdx) => buildTile(rowIdx, colIdx, piece))}
              </div>
            )
          }
          )
        }
      </div>
    </div>
  );
}
