import axios from 'axios'
import State from '../state/State'

const PROD = false

const PROD_BACKEND_URL = 'https://chess-backend-api.herokuapp.com/'
const BACKEND_URL = PROD ? PROD_BACKEND_URL : `http://localhost:7000`

const axiosI = axios.create({
    baseURL: BACKEND_URL,
    headers: {
        'Access-Control-Allow-Origin': '*',
        'Access-Control-Allow-Methods': 'GET,PUT,POST,DELETE,PATCH,OPTIONS',
        'Authorization': 'Bearer ' + State.getAppState().authentication.token
    },
    validateStatus: () => true,
})

const config = {
    SERVER_URL: BACKEND_URL,
    AXIOSINSTANCE: axiosI
}

export default config;
