import './Login.css'
import CONFIG from '../../../server/config' 
import { attemptLogin } from '../Authentication'
import { useRef } from "react";
import Swal from 'sweetalert2'
import Swal1 from 'sweetalert2/dist/sweetalert2.js'

export default function Login() {
    const username: any = useRef()
    const password: any = useRef()

    const handleLogin = async () => {
        const success = await attemptLogin(username.current.value, password.current.value)
        console.log(success)
        if (success === true) {
            window.location.href = '/'
            return
        }
        Swal.fire({
            title: 'Invalid Credentials',
            icon: 'error',
            iconHtml: 'X',
            confirmButtonText: 'OK',
          })
    }

    return (
        <div className="global-container">
            <div className="auth-container">
                <form>
                    <label htmlFor="auth-username">USERNAME</label>
                    <input ref={username} type="text" className="auth-input" id="auth-username" name="username"/><br/>

                    <label htmlFor="auth-password">PASSWORD</label>
                    <input ref={password} type="password" className="auth-input" id="auth-password" name="password"/><br/>

                    <input id="auth-button" value="LOGIN" onClick={handleLogin}/>
                    <a className="signup-link" href="/signup">Don't have an account yet? Click here.</a>
                </form>
            </div>
        </div>
    )
}
