import './GamePage.css'

import GameVS from '../../components/GameVS/GameVS'
import GameInfo from '../../components/GameInfo/GameInfo'
import GameCountdown from '../../components/GameCountdown/GameCountdown'
import GameChat from '../../components/GameChat/GameChat'
import GameButtons from '../../components/GameButtons/GameButtons'
import Board from '../../components/ChessBoard/Board/Board'
import CONFIG from '../../server/config'
import axios from 'axios'

import { useSelector, useDispatch } from 'react-redux'
import { AppState } from '../../state/reducers/root'

import { useParams } from "react-router-dom";
import { useState, useEffect } from 'react';

export default function GamePage() {
  
  const { game_id } = useParams()

  // const [state, setState] = useState(fetchGameData())
  
  const fetchGameData = async () => {
    // Login
    const response = await axios.post(CONFIG.SERVER_ADDR + `/auth`, {
      username: 'J3ZERA',
      password: 'mypass'
    })
    console.log(response)
    const remoteGame = await axios.get(CONFIG.SERVER_ADDR + `/games?id=${game_id}`)
    console.log(remoteGame)
  }

  useEffect(() => {
    fetchGameData()
  }, [])

  const initialState = useSelector((state: AppState) => state)

  return (
    <div className="global-container">
      {game_id === null ? (
        'Not game'
      ) : (
        <div className="game-container">
          <div className="game-container-row">
            <GameVS player1={initialState.game.player_white} player2={initialState.game.player_black}/>
            <GameCountdown initalTime={0} />
          </div>
          <div className="game-container-row">
            <Board />
            <GameChat game_id={game_id!!} player1={initialState.game.player_white} player2={initialState.game.player_black}/>
          </div>
          <div className="game-container-row">
            <GameInfo game_id={game_id!!} player2={initialState.game.player_black} />
            <GameButtons />
          </div>
        </div>
      )
      
    }
      
    </div>
  )
}
