import './GamePage.css'

import GameVS from '../../components/GameVS/GameVS'
import GameInfo from '../../components/GameInfo/GameInfo'
import GameCountdown from '../../components/GameCountdown/GameCountdown'
import GameChat from '../../components/GameChat/GameChat'
import GameButtons from '../../components/GameButtons/GameButtons'
import Board from '../../components/ChessBoard/Board/Board'

import { useSelector, useDispatch } from 'react-redux'
import { AppState } from '../../state/reducers/root'

import { useParams } from "react-router-dom";
import { useState } from 'react';

export default async function GamePage(props: any) {
  
  const { game_id } = useParams()
  
  // Fetch data from game and update the state. Only then render the html
  const fetchGameData = () => {
    
  }

  const initialState = useSelector((state: AppState) => state)
  const [state, setState] = useState(await fetchGameData())

  return (
    <div className="global-container">
      {game_id === null ? (
        'Not game'
      ) : (
        <div className="game-container">
          <div className="game-container-row">
            <GameVS player1={state.player1} player2={state.player2}/>
            <GameCountdown initalTime={0} />
          </div>
          <div className="game-container-row">
            <Board />
            <GameChat game_id={game_id!} player1={state.player1} player2={state.player2}/>
          </div>
          <div className="game-container-row">
            <GameInfo game_id={game_id!} player2={state.player2} />
            <GameButtons />
          </div>
        </div>
      )
      
    }
      
    </div>
  )
}
