import './Login.css'
import '../Authentication.css'

import { attemptLogin } from '../Authentication'
import { useRef } from "react";
import Swal from 'sweetalert2'
import { useNavigate } from "react-router-dom";

export default function Login(props: any) {
    const username: any = useRef()
    const password: any = useRef()
    const navigate = useNavigate()

    function showSuccess() {
        const headerStyle = {
            justifyContent: 'space-around',
            width: '100%'
        }

        return (
            <div className="modal fade" id="message-modal" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog" role="document">
                    <div className="modal-content">
                        <div className="modal-header" style={headerStyle}>
                            <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div className="modal-body">
                            CONTENT GOES HERE
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Save changes</button>
                        </div>
                    </div>
                </div>
            </div >
        )
    }

    const handleLogin = async () => {
        const usernameValue = username.current.value
        const passwordValue = password.current.value
        if (usernameValue.length === 0 || passwordValue.length === 0)
            return
        const result = await attemptLogin(usernameValue, passwordValue)
        if (result === true) {
            await Swal.fire({
                title: 'Success',
                icon: 'success',
                confirmButtonText: 'OK',
            })
            navigate('/')
            return
        }
        Swal.fire({
            title: result.message,
            icon: 'error',
            iconHtml: 'X',
            confirmButtonText: 'OK'
        })
    }

    return (
        <div className="global-container">
            <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#message-modal">
                Launch demo modal
            </button>
            {showSuccess()}
            <div className="auth-container">
                <form>
                    <label htmlFor="auth-username">USERNAME</label>
                    <input ref={username} type="text" className="auth-input" id="auth-username" name="username" /><br />

                    <label htmlFor="auth-password">PASSWORD</label>
                    <input ref={password} type="password" className="auth-input" id="auth-password" name="password" onKeyDown={e => { if (e.key === "Enter") handleLogin() }} /><br />

                    <input id="auth-button" value="LOGIN" onClick={handleLogin} />
                    <a className="signup-link" href="/signup">Don't have an account yet? Click here.</a>
                </form>
            </div>
        </div>
    )
}
