import { PieceColor } from '../../domain/piece'

export interface AppState {
    authentication: Authentication,
    game: Game
}

export interface Authentication {
    isAuthenticated: boolean,
    username: string | null
}

export interface Game {
    game_id: string | null,
    local_player_pieces: PieceColor | null
}

const initialState: AppState = {
    authentication: {
        isAuthenticated: false,
        username: null
    },
    game: {
        game_id: null,
        local_player_pieces: null
    }
}

export default function rootReducer(state: AppState = initialState, action: any): AppState {
    switch (action.type) {
        case 'LOGIN':
            return {
                ...state,
                authentication: {
                    isAuthenticated: true,
                    username: action.payload.username
                }
            }
        case 'LOGOUT':
            return initialState
        case 'JOIN_GAME':
            return {
                ...state,
                game: {
                    game_id: action.payload.game_id,
                    local_player_pieces: action.payload.local_player_pieces
                }
            }
        case 'LEAVE_GAME':
            return {
                ...state,
                game: {
                    game_id: null,
                    local_player_pieces: null
                }
            }
        default:
            return state
    }
}
