import './board.css'

import Tile, { TileColor } from '../Tile/Tile';
const BOARD_WIDTH = 8;
const BOARD_HEIGHT = 8;

export default function Board() {

  const generateBoard = () => {
    const rowContents = []
    const board: Array<Array<any>> = Array(BOARD_HEIGHT).fill(null).map(()=>Array(BOARD_WIDTH).fill(null));
    return (
      <div>
        {board.map((row, rowIdx) => {
            return row.map((col, colIdx) => {
              console.log(rowIdx, colIdx)
              return (
                <div>
                  <Tile color={calcTileColor(rowIdx, colIdx)}/>
                  {colIdx === BOARD_WIDTH - 1 ? <div/> : null}
                </div>
              )}
            )}
          )
        }
      </div>
    )
  }

  const calcTileColor = (row: number, col: number) => (col + row) % 2 === 0 ? TileColor.LIGHT : TileColor.DARK

  return (
    <div className="board">
        {generateBoard()}
    </div>
  );
}
