import Actions from './actions/action'
import { store as Store } from '../index'
import Server from '../server/server'

export function joinGame(game_id: string) {
    return function (dispatch: any, getState: any) {
        Server.getGame(game_id)
            .then(game => {
                // ! PieceColor.WHITE IS HARDCODED, LATER CALCULATE IT FROM COMPARING THE STATE USERNAME WITH THE PLAYER_WHITE AND PLAYER_BLACK
                // const username = (getState() as AppState).authentication.username
                dispatch(Actions.joinGame(game))
            })
    }
}

export function login(username: string) {
    Store.dispatch(Actions.doLogin(username))
}

export function logout(username: string, password: string) {
    Store.dispatch(Actions.doLogout)
}

export function leaveGame() {
    Store.dispatch(Actions.leaveGame())
}

const State = {
    joinGame,
    login,
    logout,
    leaveGame
}

export default State
