import './OpenGame.css'

import { useRef, useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'

import { Game } from '../../../../state/reducers/root'

import Server, { ServerResponse } from '../../../../server/server'
import Alerts from '../../../../components/Alerts/sa-alerts'

export default function OpenGame() {
    const navigate = useNavigate()

    const gameId_input: any = useRef()

    const openGameBtn: any = useRef()

    const [gameId, setGameId] = useState("")

    async function handleOpenGame() {
        const gameIdValue = validateAndSetGameID()
        if (gameIdValue == false)
            return

        const result: ServerResponse = await Server.createGame(gameIdValue)

        if (result.success)
            Alerts.showNotification("Game Created! You can join now.")
    }

    function handleJoinGame() {
        const gameIdValue = validateAndSetGameID()
        if (gameIdValue == false)
            return
        navigate(`/game/${gameIdValue}`)
    }


    function validateAndSetGameID(): false | string {
        const gameIdValue = gameId_input.current.value
        if (gameIdValue.length === 0) {
            return false
        }
        setGameId(gameIdValue)
        return gameIdValue
    }

    function handleInputChange() {
        validateAndSetGameID()
    }

    return (
        <div className="open-game-inner-container">
            <h3 className="header">Open a Game</h3>

            <div className="entry">
                <span className="key">Game ID</span>
                <span className="value">
                    <input onChange={handleInputChange} ref={gameId_input} className="game-id form-control" placeholder="Game ID" aria-label="Game ID" aria-describedby="basic-addon1" />
                </span>
            </div>

            <div className="entry">
                <button ref={openGameBtn} className="open-game-btn" onClick={handleOpenGame}>
                    OPEN
                </button>

                <button ref={openGameBtn} className="open-game-btn" onClick={handleJoinGame}>
                    JOIN
                </button>
            </div>
        </div>
    )
    /*
    {
        isGameOpened ?
            <div className="entry">
                <Link to={`/game/${gameId}`}>
                    <button ref={openButton} className="open-game-btn" >
                        ENTER GAME
                    </button>
                </Link>
            </div>
            : null
    }
    */
}
