import CONFIG from '../../server/config'

import { doLogin } from '../../state/actions/action'
import { store } from '../../index'
import Server from '../../server/server'

export async function attemptLogin(username: string, password: string) {
    const result = Server.tryLogin(username, password)
}

export async function attemptSignup(username: string, password: string) {
    const loginResponse = await CONFIG.AXIOSINSTANCE.post('/users', {
        'username': username,
        'password': password
    })
    if (loginResponse.status === 201) {
        store.dispatch(doLogin(username))
        return true
    }
    else
        return loginResponse.data.error
}
