import CONFIG from "../../server/config"

export const doLogin = (username: string) => {
    return {
        type: 'LOGIN',
        payload: {
            'username': username
        }
    }
}

// Currently not being used
export const doLogout = () => {
    // To remove the session cookie
    // Server.logout()
    CONFIG.AUTH_TOKEN = null
    return {
        type: 'LOGOUT',
        payload: {}
    }
}

export const clearState = () => {
    CONFIG.AUTH_TOKEN = null
    return {
        type: 'CLEAR_STATE',
        payload: {}
    }
}

const Actions = {
    doLogin,
    doLogout,

    clearState
}

export default Actions
