import './PlayerMenu.css'
import '../AuthButtons.css'

import { doLogout } from '../../../state/actions/action'
import { store } from '../../../index'

import { useState } from 'react';

export default function PlayerMenu(props: any) {
    const username = props.username
    const [isActive, setActive] = useState(true)

    function handleLogout() {
        store.dispatch(doLogout())
    }

    function toggleMenu() {
        const prevState = isActive
        setActive(!prevState)
    }

    return (
        <div className="btn-group">
            <button className="auth-button auth-playermenu-button dropdown-toggles" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                {username}
            </button>
            {
                isActive ?
                    <div className="player-menu dropdown-menu">
                        <button className="btn my-profile-button dropdown-item">
                            Profile
                        </button>
                        <button className="btn logout-button dropdown-item" onClick={handleLogout}>
                            Logout
                        </button>
                    </div>
                    : null
            }
        </div>
    )
}
