import { PieceColor } from '../../domain/piece'

const initialState: AppState = {
    authentication: {
        isAuthenticated: false,
        username: null
    },
}

export default function rootReducer(state: AppState = initialState, action: any): AppState {
    switch (action.type) {
        case 'LOGIN':
            return {
                ...state,
                authentication: {
                    isAuthenticated: true,
                    username: action.payload.username
                }
            }
        case 'LOGOUT':
            return initialState
        case 'CLEAR_STATE':
            return {
                authentication: {
                    isAuthenticated: false,
                    username: null
                }
            }
        default:
            return state
    }
}
