import './board.css'

import Tile, { TileColor } from '../Tile/Tile';
import { PieceObject, PieceType, PieceColor } from '../Piece/Piece';
const BOARD_WIDTH = 8;
const BOARD_HEIGHT = 8;

interface Position {
  column: number,
  row: number
}

// A non empty board representation
class BoardObject {

  constructor(initBoard: boolean = true) {
    if (initBoard) {
      this.initBoard()
    }
  }

  // the board itelf. a matrix with 8 rows and 8 columns
  board: Array<Array<PieceObject | null>> = Array(BOARD_HEIGHT).fill(null).map(()=>Array(BOARD_WIDTH).fill(null));

  setPieceAt = (position: Position, piece: PieceObject) => this.board[position.row][position.column] = piece

  getPieceAt = (position: Position) => this.board[position.row][position.column]

  toString() {

  }

  initBoard() {
    // BLACK PIECES (F1 -> Join Lines when working fines)
    this.board[0][0] = { color: PieceColor.BLACK, type: PieceType.ROOK };
    this.board[0][1] = { color: PieceColor.BLACK, type: PieceType.KNIGHT };
    this.board[0][2] = { color: PieceColor.BLACK, type: PieceType.BISHOP };
    this.board[0][3] = { color: PieceColor.BLACK, type: PieceType.QUEEN };
    this.board[0][4] = { color: PieceColor.BLACK, type: PieceType.KING };
    this.board[0][5] = { color: PieceColor.BLACK, type: PieceType.BISHOP };
    this.board[0][6] = { color: PieceColor.BLACK, type: PieceType.KNIGHT };
    this.board[0][7] = { color: PieceColor.BLACK, type: PieceType.ROOK };

    this.board[1][0] = { color: PieceColor.BLACK, type: PieceType.PAWN };
    this.board[1][1] = { color: PieceColor.BLACK, type: PieceType.PAWN };
    this.board[1][2] = { color: PieceColor.BLACK, type: PieceType.PAWN };
    this.board[1][3] = { color: PieceColor.BLACK, type: PieceType.PAWN };
    this.board[1][4] = { color: PieceColor.BLACK, type: PieceType.PAWN };
    this.board[1][5] = { color: PieceColor.BLACK, type: PieceType.PAWN };
    this.board[1][6] = { color: PieceColor.BLACK, type: PieceType.PAWN };
    this.board[1][7] = { color: PieceColor.BLACK, type: PieceType.PAWN };

    // WHITE PIECES (F1 -> Join Lines when working fines)
    this.board[6][0] = { color: PieceColor.WHITE, type: PieceType.PAWN };
    this.board[6][1] = { color: PieceColor.WHITE, type: PieceType.PAWN };
    this.board[6][2] = { color: PieceColor.WHITE, type: PieceType.PAWN };
    this.board[6][3] = { color: PieceColor.WHITE, type: PieceType.PAWN };
    this.board[6][4] = { color: PieceColor.WHITE, type: PieceType.PAWN };
    this.board[6][5] = { color: PieceColor.WHITE, type: PieceType.PAWN };
    this.board[6][6] = { color: PieceColor.WHITE, type: PieceType.PAWN };
    this.board[6][7] = { color: PieceColor.WHITE, type: PieceType.PAWN };

    this.board[7][0] = { color: PieceColor.WHITE, type: PieceType.ROOK };
    this.board[7][1] = { color: PieceColor.WHITE, type: PieceType.KNIGHT };
    this.board[7][2] = { color: PieceColor.WHITE, type: PieceType.BISHOP };
    this.board[7][3] = { color: PieceColor.WHITE, type: PieceType.QUEEN };
    this.board[7][4] = { color: PieceColor.WHITE, type: PieceType.KING };
    this.board[7][5] = { color: PieceColor.WHITE, type: PieceType.BISHOP };
    this.board[7][6] = { color: PieceColor.WHITE, type: PieceType.KNIGHT };
    this.board[7][7] = { color: PieceColor.WHITE, type: PieceType.ROOK };
  }
}

export default function Board(props: {board ?: BoardObject}) {

  const generateBoard = () => {
    const calcTileColor = (row: number, col: number) => (col + row) % 2 === 0 ? TileColor.LIGHT : TileColor.DARK
    // If a board is not passed in draw an empty board
    let baseBoard = props.board === undefined ? new BoardObject() : props.board;
    return (
      <div>
        {
          baseBoard.board.map((row, rowIdx) => {
            return (
              <div className="board-row">
                {
                row.map((col, colIdx) => {
                  return (
                    <Tile color={calcTileColor(rowIdx, colIdx)} piece={{color: PieceColor.BLACK, type: PieceType.ROOK}}/>
                  )
                }
                )}
              </div>
            )}
            )
      }
      </div>
    )
  }


  return (
    <div className="board">
        {generateBoard()}
    </div>
  );
}
