export enum PieceColor { 
    BLACK = 'b', 
    WHITE = 'w'
}

export enum PieceType {
    PAWN = 'p', 
    KING = 'k', 
    QUEEN = 'q', 
    ROOK = 'r', 
    KNIGHT = 'n',
    BISHOP = 'b'
}

export function charToPiece(char: string): PieceObject | null {
    if (char.length != 1)
        return null
    // returns the element if it finds it
    const piece = [PieceType.PAWN, PieceType.KING, PieceType.QUEEN, PieceType.ROOK, PieceType.KNIGHT, PieceType.BISHOP]
    .find(elem => char.toLowerCase() === elem)
    if (piece === undefined)
        return null
    return {
        color: char.toUpperCase() === char ? PieceColor.WHITE : PieceColor.BLACK,
        type: piece
    }
}

export interface PieceObject {
    color: PieceColor,
    type: PieceType
}
