import './board.css'

import Tile, { TileColor } from '../Tile/Tile';
import { BoardObject, stringToBoard } from '../../../domain/board'
import { Position } from '../../../domain/position'
import Alerts from '../../Alerts/sa-alerts';
import { ErrorObject } from '../../../domain/errors';

export default function Board(
  props: {
    boardString?: string
  }
) {

  const board = props.boardString === undefined
    ? new BoardObject()
    : convertStringToBoard(boardString);

  function convertStringToBoard(str: string): BoardObject {
    try {
      const convertedBoard = stringToBoard(str)
    } catch (e: any) {
      const err = e as ErrorObject
      Alerts.showNotification(`[DEBUG] Invalid Board: ${str.length} => ${str}\nOriginal Error: ${err.message}`)
    }
  }

  function handleTileClick() {

  }

  const generateBoard = () => {
    const calcTileColor = (row: number, col: number) => (col + row) % 2 === 0 ? TileColor.LIGHT : TileColor.DARK
    // If a board is not passed in draw an empty board

    return (
      <div>
        {
          board?.board.map((row, rowIdx) => {
            return (
              <div className="board-row">
                {
                  row.map((col, colIdx) => {
                    return (
                      <Tile
                        selected={false}
                        column={colIdx}
                        row={rowIdx}
                        onTileClick={(col, row) => console.log("Captured tile click on board:", col, row)}
                        color={calcTileColor(rowIdx, colIdx)}
                        piece={board.getPieceAt(Position(colIdx, rowIdx))}
                      />
                    )
                  })
                }
              </div>
            )
          }
          )
        }
      </div>
    )
  }


  return (
    <div className="board">
      {generateBoard()}
    </div>
  );
}
