import './GamePage.css'

import GameVS from '../../components/GameVS/GameVS'
import GameInfo from '../../components/GameInfo/GameInfo'
import GameCountdown from '../../components/GameCountdown/GameCountdown'
import GameChat from '../../components/GameChat/GameChat'
import GameButtons from '../../components/GameButtons/GameButtons'
import Board from '../../components/ChessBoard/Board/Board'

import { Game } from '../../state/reducers/root'
import State from '../../state/State'

import { useParams, useNavigate } from "react-router-dom";
import { SetStateAction, useEffect, useState } from 'react';
import { Dispatch } from 'redux'

export default function GamePage(props: any) {

  const navigate = useNavigate()
  const game_id = useParams().game_id!!

  interface InternalGameState {
    type: (prevState: InternalGameState) => InternalGameState
    game: Game | null
  }

  const initialGame: Game | null = null
  var game: InternalGameState = {
    game: null,
    type: (prevState) => prevState
  }
  var setGame: Dispatch<SetStateAction<InternalGameState>>

  [game, setGame] = useState(game)

  if (game_id == null) {
    navigate('/')
  }

  useEffect(() => {
    State.joinGame(game_id)
    console.log("USE EFFECT RAN")
    const appState = State.GetAppState()
    console.log()
    setGame(appState.game)
  }, [])

  return (
    <div className="global-container">
      {game == null ? (
        'Not game'
      ) : (
        <div className="game-container">
          <div className="game-container-row">
            <GameVS player1={game.player_white} player2={game.player_black} />
            <GameCountdown initalTime={0} />
          </div>
          <div className="game-container-row">
            <Board />
            <GameChat game_id={game_id!!} player1={game.player_white} player2={game.player_black} />
          </div>
          <div className="game-container-row">
            <GameInfo game_id={game_id!!} player2={game.player_black} />
            <GameButtons />
          </div>
        </div>
      )
      }
    </div>
  )
}
