import './PlayerMenu.css'
import '../AuthButtons.css'

import { doLogout } from '../../../state/actions/action'
import { store } from '../../../index'

import { useState } from 'react';

export default function PlayerMenu(props: any) {
    const username = props.username
    const [isActive, setActive] = useState(false)

    function handleLogout() {
        store.dispatch(doLogout())
    }

    function toggleMenu() {
        const prevState = isActive
        setActive(!prevState)
    }

    return (
        <>
            <button className="auth-button auth-playermenu-button" onClick={toggleMenu}>
                {username}
            </button>
            {
                isActive ?
                    <div className="player-menu">
                        <button className="my-profile-button">

                        </button>
                        <button className="logout-button" onClick={handleLogout}>
                            Logout
                        </button>
                    </div>
                    : null
            }
        </>
    )
}
