import Actions from './actions/action'
import { store as Store } from '../index'
import Server, { ServerResponse } from '../server/server'
import { AppState, Game } from './reducers/root'

export function GetAppState(): AppState {
    return Store.getState()
}

export function joinGame(game_id: string, callback: (game: Game) => void) {
    // Since we are joining a new game 
    Store.dispatch(Actions.leaveGame())

    Store.dispatch(function (dispatch: any, getState: any) {
        Server.getGame(game_id)
            .then((response: ServerResponse) => {
                // Game Exists
                if (response.success) {
                    const gameObject = response.data as Game
                    dispatch(Actions.joinGame(gameObject))
                    callback(gameObject)
                }
            })
    })
}

export function login(username: string) {
    Store.dispatch(Actions.doLogin(username))
}

export function logout() {
    Store.dispatch(Actions.doLogout())
}

export function leaveGame() {
    Store.dispatch(Actions.leaveGame())
}

export function clearState() {
    Store.dispatch(Actions.clearState())
}

const State = {
    GetAppState,
    joinGame,
    login,
    logout,
    leaveGame
}

export default State
