import swal from 'sweetalert'


export function showMessage(title: string, message: string) {
    swal({
        title: title,
        text: message
    })
}

export function showNotification(message: string, duration: number = 3500) {
    swal("This modal will disappear soon!", {
        buttons: false,
        timer: 3000,
    });
}

const Alerts = {
    showMessage,
    showNotification
}

export default Alerts
