import CONFIG from '../../server/config'

import State from '../../state/State'
import Server from '../../server/server'


export async function attemptLogin(username: string, password: string) {
    const result = await Server.tryLogin(username, password)

    if (result == true) {
        // CALL STORE HERE 
        State.login(username)
    } else {
        alert(result)
    }
}

export async function attemptSignup(username: string, password: string) {
    const result = await Server.trySignup(username, password)

    if (result == true) {
        // CALL STORE HERE 
    } else {
        alert(result)
    }
}
