import { BoardObject } from '../../domain/board'
import { PieceColor } from '../../domain/piece'
import CONFIG from '../../server/config'
import { AppState } from '../reducers/root'

export const doLogin = (username: string) => {
    return {
        type: 'LOGIN',
        payload: {
            'username': username
        }
    }
}

export const doLogout = () => {
    CONFIG.AXIOSINSTANCE.get("logout")

    return {
        type: 'LOGOUT',
        payload: {}
    }
}

export const joinGame = (game_id: string, piecesColor: PieceColor, board: BoardObject, player_white: PieceColor, player_black: PieceColor) => {
    console.log("Action GOT: ", game_id, piecesColor, board, player_white, player_black)
    return {
        type: 'JOIN_GAME',
        payload: {
            'game_id': game_id,
            'local_player_pieces': piecesColor,
            'board': board,
            'player_white': player_white,
            'player_black': player_black
        }
    }
}

export function getRemoteGameData(game_id: string) {
    return function (dispatch: any, getState: any) {
        CONFIG.AXIOSINSTANCE.get(`/games?id=${game_id}`).then(res => {
            if (res.status != 200) {
                console.error(res.data)
                return
            }
            return res.data.data
        }).then((data) => {
            // ! PieceColor.WHITE IS HARDCODED, LATER CALCULATE IT FROM COMPARING THE STATE USERNAME WITH THE PLAYER_WHITE AND PLAYER_BLACK
            // const username = (getState() as AppState).authentication.username
            dispatch(joinGame(game_id, PieceColor.WHITE, data.board, data.player_white, data.player_black))
        })
    }
}

export const leaveGame = () => {
    return {
        type: 'LEAVE_GAME',
        payload: {}
    }
}
