import { RemoteGame } from '../../state/reducers/root'
import './GameInfo.css'

export default function GameInfo(
    props: {
        game_over: boolean
        turn_username: string,
        winner_username: string | null
        opponent_username: string | null
    }
) {
    /* UPDATE THE SPECTATORS HERE */

    return (
        <div className="game-info">
            {
                props.opponent_username == null ?
                    <div className="game-info-box">
                        <div className="game-info-prop">
                            Waiting for an opponent
                        </div>
                    </div>
                    : null
            }

            {
                !props.game_over ?
                    <div className="game-info-box">
                        <div className="game-info-prop">
                            Turn: {props.turn_username}
                        </div>
                    </div> : null
            }


            {
                props.winner_username != null ?
                    <>
                        <div className="game-info-box">
                            <div className="game-info-prop">
                                Winner: {props.winner_username}
                            </div>
                        </div>
                    </>
                    : null
            }


            <div className="game-info-box">
                <div className="game-info-prop">
                    Spectators:
                </div>
                <div className="game-info-value">
                    0
                </div>
            </div>
        </div>
    )
}
