import './board.css'

import Tile, { TileColor } from '../Tile/Tile';
import { BoardObject, stringToBoard } from '../../../domain/board'
import { Position } from '../../../domain/position'

export default function Board(
  props: {
    boardString?: string
  }
) {

  const generateBoard = () => {
    const calcTileColor = (row: number, col: number) => (col + row) % 2 === 0 ? TileColor.LIGHT : TileColor.DARK
    // If a board is not passed in draw an empty board
    const baseBoard = props.boardString === undefined
      ? new BoardObject()
      : stringToBoard(props.boardString);
    return (
      <div>
        {
          baseBoard?.board.map((row, rowIdx) => {
            return (
              <div className="board-row">
                {
                  row.map((col, colIdx) => {
                    return (
                      <Tile
                        selected={false}
                        column={colIdx}
                        row={rowIdx}
                        onTileClick={(col, row) => console.log("Captured tile click on board:", col, row)}
                        color={calcTileColor(rowIdx, colIdx)}
                        piece={baseBoard.getPieceAt(Position(colIdx, rowIdx))}
                      />
                    )
                  })
                }
              </div>
            )
          }
          )
        }
      </div>
    )
  }


  return (
    <div className="board">
      {generateBoard()}
    </div>
  );
}
