import './GamePage.css'

import GameVS from '../../components/GameVS/GameVS'
import GameInfo from '../../components/GameInfo/GameInfo'
import GameCountdown from '../../components/GameCountdown/GameCountdown'
import GameChat from '../../components/GameChat/GameChat'
import GameButtons from '../../components/GameButtons/GameButtons'
import Board from '../../components/ChessBoard/Board/Board'

import { RemoteGame } from '../../state/reducers/root'
import State from '../../state/State'

import { useParams, useNavigate, NavigateFunction } from "react-router-dom";
import { useEffect, useState } from 'react';
import Alerts from '../../components/Alerts/sa-alerts'
import { PieceColor } from '../../domain/piece'
import Server, { ServerResponse, ServerError } from '../../server/server'


interface IntGameState {
  id: string | null
  local_player_username: string
  local_player_pieces: PieceColor

  opponent_username: string | null
  opponent_pieces: PieceColor | null

  winner_username: string | null
  winner_pieces: PieceColor | null

  turn_username: string
}

interface InternalGameStateWrapper {
  game: IntGameState | null
}

const initialGameState: InternalGameStateWrapper = {
  game: null
}

enum LOCAL {
  PLAYER_WHITE,
  PLAYER_BLACK,
  VIEWER,
  UNKNOWN
}

export default function GamePage(props: any) {

  const navigate = useNavigate()
  // NEVER NULL BECAUSE OF THE ROUTE POINTING TO THIS COMPONENT 
  const game_id = useParams().game_id!!
  const [gameState, setGameState] = useState(initialGameState)
  const username = State.GetAppState().authentication.username
  const [local, setLocal] = useState(LOCAL.UNKNOWN)

  // console.log(gameState.game)
  // console.log(LOCAL[local])


  // Run when the component first renders
  useEffect(() => {
    joinGame()

    setInterval(updateFromRemote, 2000)

  }, [])

  async function updateFromRemote() {
    console.log("Trying to update from remote")
    console.log(gameState.game)
    if (gameState.game?.id == null)
      return
    // Do not auto-refresh if it's MY turn
    /*
    if (gameState.game.turn_username == username || local == LOCAL.VIEWER)
      return
    */
    const response = await Server.getGame(gameState.game.id)
    console.log(response)
    if (response.success) {
      const remoteGame = response.data as RemoteGame
      console.log(remoteGame)
    } else {
      const error = response.data as ServerError
      console.log(error)
    }
  }

  async function joinGame() {
    const response = await State.joinGame(game_id)

    if (response.success) {
      const remoteGame = response.data as RemoteGame
      const wai = calculateLocal(remoteGame, username)

      setLocal(wai)

      if (wai == LOCAL.PLAYER_BLACK) {
        // Tell the server i am the PLAYER_BLACK
        Server.connectToGame(game_id)
          .then(res => {
            if (res.success) {
              const upGame = res.data as RemoteGame
              setGameState(createInternalGameStateFromRemote(local, upGame))
            } else {
              Alerts.showNotification((res.data as ServerError).message)
              console.log(res.data)
            }
          })
      } else {
        setGameState(createInternalGameStateFromRemote(local, remoteGame))
      }
    } else {
      const errorResponse = response.data as ServerError
      showJoinGameError(game_id, navigate, errorResponse.message)
    }
  }

  return (
    <div className="global-container">
      {
        gameState.game == null ? (
          null
        ) : (
          <div className="game-container">
            <div className="game-container-row">
              <h1 className="game-id-header">Game ID : {gameState.game.id}</h1>
            </div>
            <div className="game-container-row">
              <GameVS player1={gameState.game.local_player_username} player2={gameState.game.opponent_username} />
              <GameCountdown initalTime={60} />
            </div>
            <div className="game-container-row">
              <Board />
              <GameChat game_id={game_id} player1={gameState.game.local_player_username} player2={gameState.game.opponent_username} />
            </div>
            <div className="game-container-row">
              <GameInfo turn_username={gameState.game.turn_username} opponent_username={gameState.game.opponent_username} />
              <GameButtons />
            </div>
          </div>
        )
      }
    </div >
  )
}


function createInternalGameStateFromRemote(local: LOCAL, remoteGS: RemoteGame): InternalGameStateWrapper {
  var local_player: string

  var local_player_username: string | null

  if (local == LOCAL.PLAYER_WHITE) {
    local_player_username = remoteGS.player_white
  }
  else if (local == LOCAL.PLAYER_BLACK) {
    local_player_username = remoteGS.player_black!!
  } else {
    local_player_username = remoteGS.player_white
  }

  var local_player_pieces = remoteGS.player_white == local_player_username ? PieceColor.WHITE : PieceColor.BLACK
  const opponent_pieces = local_player_pieces == PieceColor.WHITE ? PieceColor.BLACK : PieceColor.WHITE

  const opponent_username = remoteGS.player_white == local_player_username ? remoteGS.player_black : remoteGS.player_white

  var winner_pieces = null

  if (remoteGS.winner != null) {
    if (remoteGS.winner == local_player_username) {
      winner_pieces = local_player_pieces
    } else {
      winner_pieces = opponent_pieces
    }
  }

  const turn_username = remoteGS.turn == local_player_pieces ? local_player_username : opponent_username

  return {
    game: {
      id: remoteGS._id,

      local_player_username: local_player_username,
      local_player_pieces: local_player_pieces,

      opponent_username: opponent_username,
      opponent_pieces: opponent_pieces,

      winner_username: remoteGS.winner,
      winner_pieces: winner_pieces,

      turn_username: turn_username != null ? turn_username : 'Opponent',
    }
  }
}

// Utils functions for this component

function showJoinGameError(game_id: string, navigate: NavigateFunction, message: string) {
  Alerts.showMessage(`Game Error: "${game_id}"`, message,
    {
      goBackBtn: {
        text: 'Go Back',
        className: 'go-back-btn',
        visible: true
      }
    }, [
    {
      className: "go-back-btn",
      eventName: "click",
      execute: () => { navigate('../') }
    }
  ])
}

function calculateLocal(gameState: RemoteGame, username: string | null): LOCAL {
  if (username == null) {
    return LOCAL.VIEWER
  } else if (username == gameState.player_white) {
    return LOCAL.PLAYER_WHITE
  } else {
    // If i am not a viewer and not the game creator (WHITE) then auto join the game as BLACK
    return LOCAL.PLAYER_BLACK
  }
}
