// Utilitary functions for the GamePage component

import { NavigateFunction } from "react-router-dom"
import Alerts from "../../components/Alerts/sa-alerts"
import { PieceColor } from "../../domain/piece"
import { RemoteGame } from "../../state/reducers/root"
import { CLIENT_TYPE, InternalGameState, InternalGameStateWrapper } from "./GamePage"

export function showGameError(game_id: string, navigate: NavigateFunction, message: string) {
    Alerts.showMessage(`Game Error: "${game_id}"`, message,
        {
            goBackBtn: {
                text: 'Go Back',
                className: 'go-back-btn',
                visible: true
            }
        }, [
        {
            className: "go-back-btn",
            eventName: "click",
            execute: () => { navigate('../') }
        }
    ])
}

export function calcClientType(gameState: RemoteGame, username: string | null): CLIENT_TYPE {
    if (username == null) {
        return CLIENT_TYPE.VIEWER
    } else if (username == gameState.player1) {
        return CLIENT_TYPE.PLAYER_WHITE
    } else {
        // If i am not a viewer and not the game creator (WHITE) then auto join the game as BLACK
        return CLIENT_TYPE.PLAYER_BLACK
    }
}


export function getInternalGameStateFromRemote(
    clientType: CLIENT_TYPE,
    remoteGS: RemoteGame
): InternalGameStateWrapper {
    var local_player_username: string | null

    if (clientType == CLIENT_TYPE.PLAYER_WHITE) {
        local_player_username = remoteGS.player1
    }
    else if (clientType == CLIENT_TYPE.PLAYER_BLACK) {
        local_player_username = remoteGS.player2!!
    } else {
        local_player_username = remoteGS.player1
    }

    var local_player_pieces = remoteGS.player1 == local_player_username ? PieceColor.WHITE : PieceColor.BLACK
    const opponent_pieces = local_player_pieces == PieceColor.WHITE ? PieceColor.BLACK : PieceColor.WHITE

    const opponent_username = remoteGS.player1 == local_player_username ? remoteGS.player2 : remoteGS.player1

    var winner_pieces = null

    if (remoteGS.winner != null) {
        if (remoteGS.winner == local_player_username) {
            winner_pieces = local_player_pieces
        } else {
            winner_pieces = opponent_pieces
        }
    }

    const turn_username = remoteGS.turn == local_player_pieces ? local_player_username : opponent_username

    return {
        game: {
            id: remoteGS._id,

            local_player_username: local_player_username,
            local_player_pieces: local_player_pieces,

            opponent_username: opponent_username,
            opponent_pieces: opponent_pieces,

            winner_username: remoteGS.winner,
            winner_pieces: winner_pieces,

            turn_username: turn_username != null ? turn_username : 'Opponent',
        },
        client_type: clientType,
    }
}

const compareGames = (game1: InternalGameState, game2: InternalGameState): boolean =>
    game1.id == game2.id &&
    game1.opponent_username == game2.opponent_username &&
    game1.turn_username == game2.turn_username &&
    game1.winner_pieces == game2.winner_pieces

function isObject(object: any) {
    return object != null && typeof object === 'object';
}

const GamePageUtils = {
    showGameError,
    calcClientType,
    getInternalGameStateFromRemote,

    compareGames
}

export default GamePageUtils
