import { AxiosResponse } from 'axios'
import CONFIG from './config'
import { BoardObject } from '../domain/board'
import { Game } from '../state/reducers/root'

import Alerts from '../components/Alerts/sa-alerts'

const AXIOSI = CONFIG.AXIOSINSTANCE

// Private
interface ServerError {
    http_code: number,
    message: string
}

// Private
interface OriginalServerRespose {
    data: any
}

export interface ServerResponse {
    success: boolean,
    data: any | ServerError
}

function errorWrapper(block: (...a: any[]) => any, ...args: any[]): any {
    try {
        return block(args)
    } catch (e) {
        alert("SERVER ERROR: " + e)
    }
}



function responseIntercept(response: AxiosResponse): ServerResponse {
    if (response.status != 200) {
        const responseAsError = response.data as ServerError
        Alerts.showNotification(responseAsError.message)
        return { success: false, data: responseAsError }
    }
    return { success: false, data: response.data.data }
}


// PUBLIC FUNCTIONS
const logout = () => errorWrapper(() => {
    AXIOSI.get('/logout')
})

const getGame = (game_id: string): Promise<ServerResponse> => errorWrapper(() => {
    AXIOSI.get(`/games?id=${game_id}`)
        .then(res => responseIntercept(res))
        .then(data => {
            const
            return
        })
})

async function tryLogin(username: string, password: string) {
    const loginResponse = await CONFIG.AXIOSINSTANCE.post('/auth', {
        'username': username,
        'password': password
    })
    if (loginResponse.status === 200) {
        return true
    }
    else
        return loginResponse.data.error
}

async function trySignup(username: string, password: string) {
    const loginResponse = await CONFIG.AXIOSINSTANCE.post('/users', {
        'username': username,
        'password': password
    })
    if (loginResponse.status === 201) {
        // IMPROVE WITH REDUX STORE
        // store.dispatch(doLogin(username))
        return true
    }
    else
        return loginResponse.data.error
}

async function createGame(game_id: string) {
    const response = await CONFIG.AXIOSINSTANCE.post(`/games/`, {
        id: game_id
    })

    if (response.status === 201) {
        const gameObject = response.data.data
        return gameObject as Game
    } else {
        return response.data.error
    }
}

const Server = {
    logout,
    getGame,
    tryLogin,
    trySignup,

    createGame
}

export default Server
