export function showErrorNotification(message, centered = false, duration = 4000) {
    $.bootstrapGrowl(message, {
        type: 'danger',
        align: centered ? 'center' : 'right',
        width: 'auto',
        allow_dismiss: false,
        delay: duration,
        offset: { from: 'bottom', amount: 20 },
    });
}

export function showNotification(message, centered = false, duration = 4000) {
    $.bootstrapGrowl(message, {
        type: 'success',
        align: centered ? 'center' : 'right',
        width: 'auto',
        allow_dismiss: false,
        delay: duration,
        offset: { from: 'bottom', amount: 20 },
    });
}
