import './tile.css'
import { Piece as PieceObject } from '../../../domain/piece';
import Piece from '../Piece/Piece'

export enum TileColor { DARK, LIGHT }

export default function Tile(props: {selected: boolean, column: number, row: number, color: TileColor, piece : PieceObject | null, onTileClick: (...args: any[]) => void}) {
  
  const calcTileBackground = () => {
    if (props.selected)
      return 'tile selected'
    if (props.color === TileColor.DARK)
      return 'tile dark'
    else if (props.color === TileColor.LIGHT)
      return 'tile light'
  }

  return (
    <div className={calcTileBackground()} onClick={() => props.onTileClick(props.column, props.row)}>
      {
      props.piece === null
      ? 
      null 
      : 
      <Piece piece={props.piece}/>
      }
    </div>
  );
}
