import swal from 'sweetalert'

export function showMessage(title: string, message: string) {
    return swal({
        title: title,
        text: message,
        buttons: {
            confirm: {
                text: "Go Back",
                visible: true,
            }
        }
    })
}

export function showNotification(message: string, duration_s: number = 2.3) {
    return swal(message, {
        buttons: [false, false],
        timer: duration_s * 1000
    })
}

const Alerts = {
    showMessage,
    showNotification
}

export default Alerts
