import './GamePage.css'

import GameVS from '../../components/GameVS/GameVS'
import GameInfo from '../../components/GameInfo/GameInfo'
import GameCountdown from '../../components/GameCountdown/GameCountdown'
import GameChat from '../../components/GameChat/GameChat'
import GameButtons from '../../components/GameButtons/GameButtons'
import Board from '../../components/ChessBoard/Board/Board'

import { RemoteGame } from '../../state/reducers/root'
import State from '../../state/State'

import { useParams, useNavigate, NavigateFunction } from "react-router-dom";
import { useEffect, useState } from 'react';
import Alerts from '../../components/Alerts/sa-alerts'
import { PieceColor } from '../../domain/piece'
import Server, { ServerResponse, ServerError } from '../../server/server'

import GamePageUtils from './utils'
import { BoardError } from '../../domain/errors'
import { BoardObject, stringToBoard } from '../../domain/board'


export interface InternalGameState {
  id: string | null
  local_player_username: string
  local_player_pieces: PieceColor

  opponent_username: string | null
  opponent_pieces: PieceColor | null

  winner_username: string | null
  winner_pieces: PieceColor | null

  boardStr: string
  simBoard: BoardObject

  turn_username: string
  turn_pieces: PieceColor
}

export enum CLIENT_TYPE {
  PLAYER_WHITE,
  PLAYER_BLACK,
  VIEWER,
  UNKNOWN
}

export interface InternalGameStateWrapper {
  client_type: CLIENT_TYPE
  game: InternalGameState | null
}

const initialGameState: InternalGameStateWrapper = {
  game: null,
  client_type: CLIENT_TYPE.VIEWER
}

// LOW TIME BECAUSE ON DEVELOPMENT
const UPDATE_GAME_TIMEOUT = 1000

export default function GamePage(props: any) {
  const navigate = useNavigate()
  // NEVER NULL BECAUSE OF THE ROUTE POINTING TO THIS COMPONENT 
  const game_id = useParams().game_id!!
  const [gameState, setGameState] = useState(initialGameState)
  const username = State.GetAppState().authentication.username

  console.log(gameState.game)

  // FIRST RENDER
  useEffect(() => {
    joinGame()
    return () => { }
  }, [])

  // AUTO-REFRESH CALLS
  const [rr, setrr] = useState(true)
  const forceRR = () => { setrr(!rr) }
  useEffect(() => {
    if (gameState.game?.winner_username != null)
      return
    const timer = setTimeout(() => { updateFromRemote() }, UPDATE_GAME_TIMEOUT)

    return () => clearTimeout(timer)
  }, [gameState.game, rr])

  async function updateFromRemote() {
    if (gameState.game?.id == null)
      return
    // Do not auto-refresh if there is an OPPONENT AND it's MY turn. If no opponent wait for him to join
    if (gameState.game.turn_username == username && gameState.game.opponent_username != null)
      return

    forceRR()

    const response = await Server.getGame(game_id)

    if (response.success) {
      const updatedGame = GamePageUtils.getInternalGameStateFromRemote(gameState.client_type, response.data as RemoteGame)

      if (!GamePageUtils.areGamesEqual(gameState.game, updatedGame.game!!)) {
        setGameState(updatedGame)
      }
    } else {
      const error = response.data as ServerError
      GamePageUtils.showGameError(game_id, navigate, error.message)
    }
  }

  async function joinGame() {
    const response = await State.joinGame(game_id)

    if (response.success) {
      const remoteGame = response.data as RemoteGame
      const clientType = GamePageUtils.calcClientType(remoteGame, username)

      if (clientType == CLIENT_TYPE.PLAYER_BLACK) {
        // Tell the server i am the PLAYER_BLACK
        Server.connectToGame(game_id)
          .then(res => {
            if (res.success) {
              serverResponseToLocalGame(res, clientType)
            } else {
              Alerts.showNotification((res.data as ServerError).message)
            }
          })
      } else {
        serverResponseToLocalGame(response, clientType)
      }
    } else {
      const errorResponse = response.data as ServerError
      GamePageUtils.showGameError(game_id, navigate, errorResponse.message)
    }
  }

  function handleBoardError(err: BoardError) {
    GamePageUtils.showGameError(game_id, navigate, err.message)
  }

  function serverResponseToLocalGame(serverResponseWithGame: ServerResponse, clientType: CLIENT_TYPE = gameState.client_type) {
    setGameState(GamePageUtils.getInternalGameStateFromRemote(clientType, serverResponseWithGame.data as RemoteGame))
  }

  async function handleMakeMove(move: string) {
    // Validate Local
    try {
      gameState.game?.simBoard.makeMove(move)
    } catch (err) {
      const boardErr = err as BoardError
      Alerts.showNotification(boardErr.message)
      return
    }

    // Validate with server
    const response = await Server.makeMove(game_id, move)
    // Change local state from server game response
    serverResponseToLocalGame(response)
  }

  return (
    <div className="global-container">
      {
        gameState.game == null ? (
          null
        ) : (
          <div className="game-container">
            <div className="game-container-row">
              <h1 className="game-header id">Game ID : {game_id}</h1>
              <h3 className="game-header state">({gameState.game.winner_username == null ? "ongoing" : "over"})</h3>
              <button></button>
            </div>

            <div className="game-container-row">
              <GameVS
                local_player_username={gameState.game.local_player_username} local_player_pieces={gameState.game.local_player_pieces}
                opponent_username={gameState.game.opponent_username} opponent_pieces={gameState.game.opponent_pieces}
              />
              <GameCountdown initalTime={60} />
            </div>

            <div className="game-container-row">

              <Board
                board={gameState.game.simBoard}
                local_player_pieces={gameState.game.local_player_pieces}

                turn={gameState.game.turn_pieces}

                gameOver={gameState.game.winner_username != null}

                onMakeMove={handleMakeMove}
                onError={handleBoardError}
              />

              <GameChat game_id={game_id} local_player_username={gameState.game.local_player_username} opponent_username={gameState.game.opponent_username} />
            </div>

            <div className="game-container-row">
              <GameInfo
                turn_username={gameState.game.turn_username}

                winner_username={gameState.game.winner_username}

                opponent_username={gameState.game.opponent_username} />
              <GameButtons gameOver={gameState.game.winner_username != null} />
            </div>
          </div>
        )
      }
    </div >
  )
}
