import './GamePage.css'

import GameVS from '../../components/GameVS/GameVS'
import GameInfo from '../../components/GameInfo/GameInfo'
import GameCountdown from '../../components/GameCountdown/GameCountdown'
import GameChat from '../../components/GameChat/GameChat'
import GameButtons from '../../components/GameButtons/GameButtons'
import Board from '../../components/ChessBoard/Board/Board'

import { Game } from '../../state/reducers/root'
import State from '../../state/State'

import { useParams, useNavigate, NavigateFunction } from "react-router-dom";
import { useEffect, useState } from 'react';
import Alerts from '../../components/Alerts/sa-alerts'

interface InternalGameState {
  game: Game | null,
}

const initialGame: InternalGameState = {
  game: null
}

export default function GamePage(props: any) {

  const navigate = useNavigate()
  // NEVER NULL BECAUSE OF THE ROUTE POINTING TO THIS COMPONENT 
  const game_id = useParams().game_id!!
  const [gameState, setGameState] = useState(initialGame)
  const username = State.GetAppState().authentication

  // Run when the component first renders
  useEffect(() => {
    State.joinGame(game_id, (success, game) => {
      if (success && game != null) {
        setGameState({ game: game })
        console.log(game)
      } else {
        showGameNotFoundMessage(game_id, navigate)
      }
    })
  }, [])

  return (
    <div className="global-container">
      {
        gameState.game == null ? (
          null
        ) : (
          <div className="game-container">
            <div className="game-container-row">
              <GameVS player1={gameState.game.player_white} player2={gameState.game.player_black} />
              <GameCountdown initalTime={60} />
            </div>
            <div className="game-container-row">
              <Board />
              <GameChat game_id={game_id} player1={gameState.game.player_white} player2={gameState.game.player_black} />
            </div>
            <div className="game-container-row">
              <GameInfo game_id={game_id} me={username} opponent={gameState.game.player_black} />
              <GameButtons />
            </div>
          </div>
        )
      }
    </div >
  )
}

// Utils functions for this component

function showGameNotFoundMessage(game_id: string, navigate: NavigateFunction) {
  Alerts.showMessage("Game Not Found", `Could not find "${game_id}"`,
    {
      goBackBtn: {
        text: 'Go Back',
        className: 'go-back-btn',
        visible: true
      }
    }, [
    {
      className: "go-back-btn",
      eventName: "click",
      execute: () => { navigate('../') }
    }
  ])
}
