import './OpenGame.css'

import { useRef, useState } from 'react'
import { Link } from 'react-router-dom'


export default function OpenGame() {
    const gameId_input: any = useRef()

    const openButton: any = useRef()

    const [gameId, setGameId] = useState("")

    const [isGameOpened, setGameOpened] = useState(false)

    function handleOpenGame() {
        if (gameId_input.current.value.length === 0) {
            return
        }
        setGameId(gameId_input.current.value)

        // Try create the game
        // Handle response
        // Show error
        // If no error change state: setGameOpened(true) + ENTER GAME button links to /game/game_id

        setGameOpened(true)
    }

    function handleGameIDChange() {
        if (!isGameOpened)
            return

        if (gameId_input.current.value == '')
            setGameOpened(false)
    }

    return (
        <div className="open-game-inner-container">
            <h3 className="header">Open a Game</h3>

            <div className="entry">
                <span className="key">Game ID</span>
                <span className="value">
                    <input onChange={handleGameIDChange} ref={gameId_input} className="game-id form-control" placeholder="Game ID" aria-label="Game ID" aria-describedby="basic-addon1" />
                </span>
            </div>

            <div className="entry">
                <button ref={openButton} className="open-game-btn" onClick={handleOpenGame}>
                    OPEN
                </button>
            </div>

            {
                isGameOpened ?
                    <div className="entry">
                        <Link to={`/game/${gameId}`}>
                            <button ref={openButton} className="open-game-btn" >
                                ENTER GAME
                            </button>
                        </Link>
                    </div>
                    : null
            }

        </div>
    )
}
