import './GamePage.css'

import GameVS from '../../components/GameVS/GameVS'
import GameInfo from '../../components/GameInfo/GameInfo'
import GameCountdown from '../../components/GameCountdown/GameCountdown'
import GameChat from '../../components/GameChat/GameChat'
import GameButtons from '../../components/GameButtons/GameButtons'
import Board from '../../components/ChessBoard/Board/Board'

import { RemoteGame } from '../../state/reducers/root'
import State from '../../state/State'

import { useParams, useNavigate, NavigateFunction } from "react-router-dom";
import { useEffect, useState } from 'react';
import Alerts from '../../components/Alerts/sa-alerts'
import { PieceColor } from '../../domain/piece'


interface IntGameState {
  id: string | null
  local_player_username: string
  local_player_pieces: PieceColor

  opponent_username: string | null
  opponent_pieces: PieceColor | null

  winner_username: string | null
  winner_pieces: PieceColor | null

  turn_username: string

  amIViewer: boolean
}

interface InternalGameStateWrapper {
  game: IntGameState | null
}

const initialGameState: InternalGameStateWrapper = {
  game: null
}

export default function GamePage(props: any) {

  const navigate = useNavigate()
  // NEVER NULL BECAUSE OF THE ROUTE POINTING TO THIS COMPONENT 
  const game_id = useParams().game_id!!
  const [gameState, setGameState] = useState(initialGameState)
  const username = State.GetAppState().authentication.username


  // Run when the component first renders
  useEffect(() => {
    State.joinGame(game_id, (success, game) => {
      if (success && game != null) {
        setGameState(createInternalGameStateFromRemote(username, game))
        console.log(game)
      } else {
        showGameNotFoundMessage(game_id, navigate)
      }
    })
  }, [])

  return (
    <div className="global-container">
      {
        gameState.game == null ? (
          null
        ) : (
          <div className="game-container">
            <div className="game-container-row">
              <GameVS player1={gameState.game.local_player_username} player2={gameState.game.opponent_username} />
              <GameCountdown initalTime={60} />
            </div>
            <div className="game-container-row">
              <Board />
              <GameChat game_id={game_id} player1={gameState.game.local_player_username} player2={gameState.game.opponent_username} />
            </div>
            <div className="game-container-row">
              <GameInfo turn_username={gameState.game.turn_username} opponent_username={gameState.game.opponent_username} />
              <GameButtons />
            </div>
          </div>
        )
      }
    </div >
  )
}


function createInternalGameStateFromRemote(username: string | null, remoteGS: RemoteGame): InternalGameStateWrapper {
  var local_player: string
  var amIViewer: boolean

  if (username != null && (remoteGS.player_black == username || remoteGS.player_white == username)) {
    local_player = username;
    amIViewer = false;
  } else {
    // If not participating in the game spectate as player white
    local_player = remoteGS.player_white
    amIViewer = true;
  }

  var local_player_pieces = remoteGS.player_white == local_player ? PieceColor.WHITE : PieceColor.BLACK
  const opponent_pieces = local_player_pieces == PieceColor.WHITE ? PieceColor.BLACK : PieceColor.WHITE

  const opponent_username = remoteGS.player_white == local_player ? remoteGS.player_black : remoteGS.player_white

  var winner_pieces = null

  if (remoteGS.winner != null) {
    if (remoteGS.winner == local_player) {
      winner_pieces = local_player_pieces
    } else {
      winner_pieces = opponent_pieces
    }
  }

  const turn_username = remoteGS.turn == local_player_pieces ? local_player : opponent_username

  return {
    game: {
      id: remoteGS._id,

      local_player_username: local_player,
      local_player_pieces: local_player_pieces,

      opponent_username: opponent_username,
      opponent_pieces: opponent_pieces,

      winner_username: remoteGS.winner,
      winner_pieces: winner_pieces,

      turn_username: turn_username,

      amIViewer: amIViewer
    }
  }
}

// Utils functions for this component

function showGameNotFoundMessage(game_id: string, navigate: NavigateFunction) {
  Alerts.showMessage("Game Not Found", `Could not find "${game_id}"`,
    {
      goBackBtn: {
        text: 'Go Back',
        className: 'go-back-btn',
        visible: true
      }
    }, [
    {
      className: "go-back-btn",
      eventName: "click",
      execute: () => { navigate('../') }
    }
  ])
}
