import './tile.css'
import { Piece as PieceObject } from '../../../domain/piece';
import Piece from '../Piece/Piece'

export enum TileColor { DARK, LIGHT }

export default function Tile(props: {column: number, row: number, color: TileColor, piece : PieceObject | null, onTileClick: (...args: any[]) => void}) {
  return (
    <div className={props.color === TileColor.DARK ? 'tile dark' : 'tile light'} onClick={() => {
      console.log("Clicked on:", props.column, props.row)
      props.onTileClick(props.column, props.row)
    }}>
      {
      props.piece === null
      ? 
      null 
      : 
      <Piece piece={props.piece}/>
      }
    </div>
  );
}
