import './GamePage.css'

import GameVS from '../../components/GameVS/GameVS'
import GameInfo from '../../components/GameInfo/GameInfo'
import GameCountdown from '../../components/GameCountdown/GameCountdown'
import GameChat from '../../components/GameChat/GameChat'
import GameButtons from '../../components/GameButtons/GameButtons'
import Board from '../../components/ChessBoard/Board/Board'

import { RemoteGame } from '../../state/reducers/root'
import State from '../../state/State'

import { useParams, useNavigate, NavigateFunction } from "react-router-dom";
import { useEffect, useState } from 'react';
import Alerts from '../../components/Alerts/sa-alerts'
import { PieceColor } from '../../domain/piece'


interface IntGameState {
  id: string | null
  my_username: string
  my_pieces: PieceColor

  opponent_username: string
  opponent_pieces: PieceColor

  winner_username: string | null
  winner_pieces: PieceColor | null

  turn_username: string
}

interface InternalGameStateWrapper {
  game: IntGameState | null
}

const initialGame: InternalGameStateWrapper = {
  game: null
}

export default function GamePage(props: any) {

  const navigate = useNavigate()
  // NEVER NULL BECAUSE OF THE ROUTE POINTING TO THIS COMPONENT 
  const game_id = useParams().game_id!!
  const [remoteGameState, setRemoteGameState] = useState(initialGame)

  // Run when the component first renders
  useEffect(() => {
    State.joinGame(game_id, (success, game) => {
      if (success && game != null) {

        setRemoteGameState(createInternalGameStateFromRemote(game))

        console.log(game)
      } else {
        showGameNotFoundMessage(game_id, navigate)
      }
    })
  }, [])

  return (
    <div className="global-container">
      {
        remoteGameState.game == null ? (
          null
        ) : (
          <div className="game-container">
            <div className="game-container-row">
              <GameVS player1={remoteGameState.game.player_white} player2={remoteGameState.game.player_black} />
              <GameCountdown initalTime={60} />
            </div>
            <div className="game-container-row">
              <Board />
              <GameChat game_id={game_id} player1={remoteGameState.game.player_white} player2={remoteGameState.game.player_black} />
            </div>
            <div className="game-container-row">
              <GameInfo game_id={game_id} game={remoteGameState.game} />
              <GameButtons />
            </div>
          </div>
        )
      }
    </div >
  )
}

function createInternalGameStateFromRemote(remoteGS: RemoteGame): InternalGameStateWrapper {

}

// Utils functions for this component

function showGameNotFoundMessage(game_id: string, navigate: NavigateFunction) {
  Alerts.showMessage("Game Not Found", `Could not find "${game_id}"`,
    {
      goBackBtn: {
        text: 'Go Back',
        className: 'go-back-btn',
        visible: true
      }
    }, [
    {
      className: "go-back-btn",
      eventName: "click",
      execute: () => { navigate('../') }
    }
  ])
}
